import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './TerminalPage.css';

const TerminalPage = () => {
  const [terminalData, setTerminalData] = useState(null);
  const [isRunning, setIsRunning] = useState(false);
  const [currentLineIndex, setCurrentLineIndex] = useState(0);
  const [displayedText, setDisplayedText] = useState('');
  const [formattedText, setFormattedText] = useState([]);
  const [isComplete, setIsComplete] = useState(false);
  const [currentTheme, setCurrentTheme] = useState('classic');
  const [fontColor, setFontColor] = useState('#00ff00');
  const [typingSpeed, setTypingSpeed] = useState(50);
  const navigate = useNavigate();

  useEffect(() => {
    const data = localStorage.getItem('terminalData');
    if (data) {
      const parsedData = JSON.parse(data);
      setTerminalData(parsedData);
      setCurrentTheme(parsedData.theme || 'classic');
      setFontColor(parsedData.fontColor || '#00ff00');
      setTypingSpeed(parsedData.typingSpeed || 50);
    } else {
      navigate('/');
    }
  }, [navigate]);

  const startTerminal = () => {
    if (!terminalData || terminalData.lines.length === 0) return;

    setIsRunning(true);
    setCurrentLineIndex(0);
    setDisplayedText('');
    setFormattedText([]);
    setIsComplete(false);

    let currentStep = 0;
    const steps = [
      () => {
        setDisplayedText('root@neopass:~$ [system]\n');
        setFormattedText([{ text: 'root@neopass:~$ [system]\n', type: 'system' }]);
      },
      () => {
        setDisplayedText(prev => prev + 'Initializing NeoPass...\n');
        setFormattedText(prev => [...prev, { text: 'Initializing NeoPass...\n', type: 'system' }]);
      },
      () => {
        setDisplayedText(prev => prev + '\n');
        setFormattedText(prev => [...prev, { text: '\n', type: 'normal' }]);
      },
    ];

    const typeText = () => {
      if (currentStep < steps.length) {
        steps[currentStep]();
        currentStep++;
        setTimeout(typeText, 800);
        return;
      }

      // Now type the user's lines with > prefix
      let lineIndex = 0;
      let charIndex = 0;

      const typeUserText = () => {
        if (lineIndex >= terminalData.lines.length) {
          // Add error message at the end
          setDisplayedText(prev => prev + '\nError: Authentication required');
          setFormattedText(prev => [...prev, { text: '\nError: Authentication required', type: 'error' }]);
          setIsComplete(true);
          setIsRunning(false);
          return;
        }

        const currentLine = terminalData.lines[lineIndex];
        const prefix = '>   ';
        const fullLine = prefix + currentLine;

        if (charIndex < fullLine.length) {
          setDisplayedText(prev => prev + fullLine[charIndex]);
          charIndex++;
          setTimeout(typeUserText, typingSpeed);
        } else {
          // Move to next line
          setDisplayedText(prev => prev + '\n');
          setFormattedText(prev => [...prev, { text: fullLine + '\n', type: 'feature' }]);
          lineIndex++;
          charIndex = 0;
          setCurrentLineIndex(lineIndex);
          setTimeout(typeUserText, 200); // Pause between lines
        }
      };

      setTimeout(typeUserText, 500);
    };

    setTimeout(typeText, 500); // Initial delay
  };

  const resetTerminal = () => {
    setIsRunning(false);
    setCurrentLineIndex(0);
    setDisplayedText('');
    setFormattedText([]);
    setIsComplete(false);
  };

  const goBack = () => {
    navigate('/');
  };

  if (!terminalData) {
    return (
      <div className="terminal-page">
        <div className="loading">Loading...</div>
      </div>
    );
  }

  return (
    <div className={`terminal-page theme-${currentTheme}`}>
      <div className="terminal-window">
        <div className="terminal-header">
          <div className="terminal-buttons">
            <span className="btn close"></span>
            <span className="btn minimize"></span>
            <span className="btn maximize"></span>
          </div>
          <div className="terminal-title">NeoPass Terminal - {currentTheme.charAt(0).toUpperCase() + currentTheme.slice(1)} Theme</div>
        </div>

        <div className="terminal-body">
          <div className="terminal-content">
            <div className="prompt-line">
              <span className="user">user@terminal</span>
              <span className="path">:~$</span>
              <span className="command">start-terminal-display</span>
            </div>

            <div className="terminal-output">
              <pre className="output-text">
                {displayedText}
                {isRunning && <span className="cursor" style={{ color: fontColor }}>█</span>}
              </pre>
            </div>

            {isComplete && (
              <div className="completion-message">
                <div className="prompt-line">
                  <span className="user">user@terminal</span>
                  <span className="path">:~$</span>
                  <span className="success">✓ Display complete</span>
                </div>
              </div>
            )}
          </div>

          <div className="terminal-controls">
            <button 
              onClick={startTerminal}
              disabled={isRunning}
              className="terminal-button start-button"
            >
              {isRunning ? 'Running...' : 'Start'}
            </button>

            <button 
              onClick={resetTerminal}
              disabled={isRunning}
              className="terminal-button reset-button"
            >
              Reset
            </button>

            <button 
              onClick={goBack}
              className="terminal-button back-button"
            >
              ← Back
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TerminalPage;
